// SPDX-License-Identifier: UNLICENSED
// from https://etherscan.io/address/0xc02aaa39b223fe8d0a0e5c4f27ead9083c756cc2#code

pragma solidity ^0.8.15;

import {ERC20} from "lib-tokens/solmate/src/tokens/ERC20.sol";
import {SafeTransferLib} from "lib-tokens/solmate/src/utils/SafeTransferLib.sol";

contract WFIL is ERC20("Wrapped Filecoin", "WFIL", 18) {
    using SafeTransferLib for address;

    event Deposit(address indexed from, uint256 amount);

    event Withdrawal(address indexed to, uint256 amount);

    function deposit() public payable virtual {
        _mint(msg.sender, msg.value);

        // emit Deposit(msg.sender, msg.value);
    }

    function withdraw(uint256 amount) public virtual {
        _burn(msg.sender, amount);

        emit Withdrawal(msg.sender, amount);

        msg.sender.safeTransferETH(amount);
    }

    receive() external payable virtual {
        deposit();
    }
}

